
const MESSAGE = {
    ERROR:'ERROR',
    INFORMATION:'INFORMATION',
    WARNING:'WARNING'
}

const display = (type, message) => {
    switch (type) {
        case MESSAGE.WARNING:
            console.log('\x1b[33m%s\x1b[0m: %s', MESSAGE.WARNING, message)
            break;
        case MESSAGE.ERROR:
            console.log('\x1b[31m%s\x1b[0m: %s', MESSAGE.ERROR, message)
            break;
        case MESSAGE.INFORMATION:
            console.log('\x1b[37m%s\x1b[0m: %s', MESSAGE.INFORMATION, message)
            break;
        default:
            console.log(message)
            break;
    }
};


exports.MESSAGE = MESSAGE
exports.show = display
